import React from 'react'
import { Link } from 'react-router-dom'

import logoImg from '../assets/img/train-station.jpg'


const [products, setProducts] = useState([])
  const [isManaged, setIsManaged] = useState(false)

  const [truck1, setTruck1] = useState([])
  const [truck2, setTruck2] = useState([])
  const [truck3, setTruck3] = useState([])
  const [truck4, setTruck4] = useState([])
  const [truck5, setTruck5] = useState([])

  const setTruck = (setFunc, product, i) => {
    const time = 1000

    setTimeout(() => {
      setFunc(products => [...products, product])
      setProducts(products => products.slice(1))
    }, i * time)
  }

const manageTrucks = () => {
  products.forEach((product, i) => {
    switch(product.caminhao) {
      case 1:
        setTruck(setTruck1, product, i)
        break;
      case 2:
        setTruck(setTruck2, product, i)
        break;
      case 3:
        setTruck(setTruck3, product, i)
        break;
      case 4:
        setTruck(setTruck4, product, i)
        break;
      case 5:
        setTruck(setTruck5, product, i)
        break;
      default:
        break;
    }
  })
  setIsManaged(true)
}

const SideBar = (props) => {
  const nav = props.nav
  const query = props.query

  return (
    <div className="col-md-6 col-sm-6 col-xs-6 display-table-cell v-align box imag" id="navigation">


      <div className="admin-bar">
        <ul>
          <li className={nav === "armazem" ? "active" : null}>
            <Link to="/">
              <i className="fa fa-cubes" aria-hidden="true"></i>
              <span className="hidden-xs">Ver Tabela de Mercadorias</span>
            </Link>
          </li>
          <li className={nav === "caminhoes" ? "active" : null}>
            <Link to="/caminhoes">
              <i className="fa fa-truck-moving" aria-hidden="true"></i>
              <span className="hidden-xs">Trens</span>
            </Link>
          </li>
          <li className={nav === "rotas" ? "active" : null}>
            <Link to={`/rotas${query === 'organizado' ? '?organizado=true' : ''}`}>
              <i className="fa fa-route" aria-hidden="true"></i>
              <span className="hidden-xs">Ver Mapa</span>
            </Link>
          </li>
        </ul>

        <div className='manage-trucks'>
          <button type="button" onClick={manageTrucks} className="btn btn-primary">
            Organizar entregas
          </button>
        </div>
      </div>

    </div>


  )
}

export default SideBar
