import React, { useEffect, useState } from 'react'

import SideBar from '../../components/SideBar'
import Header from '../../components/Header'
import imgagemEscolhida from '../../assets/img/cargas.jpg'
import Enviado from '../../components/Enviado'

import { prepareProducts } from '../../api/product'

const Transporte = () => {
  const [products, setProducts] = useState([])
  const [isManaged, setIsManaged] = useState(false)

  const [train1, setTrain1] = useState([])
  const [train2, setTrain2] = useState([])
  const [train3, setTrain3] = useState([])
  const [train4, setTrain4] = useState([])
  const [train5, setTrain5] = useState([])

  const setTrain = (setFunc, product, i) => {
    const time = 1000

    setTimeout(() => {
      setFunc(products => [...products, product])
      setProducts(products => products.slice(1))
    }, i * time)
  }

  const manageTrains = () => {
    products.forEach((product, i) => {
      switch(product.transporte) {
        case 1:
          setTrain(setTrain1, product, i)
          break;
        case 2:
          setTrain(setTrain2, product, i)
          break;
        case 3:
          setTrain(setTrain3, product, i)
          break;
        case 4:
          setTrain(setTrain4, product, i)
          break;
        case 5:
          setTrain(setTrain5, product, i)
          break;
        default:
          break;
      }
    })
    setIsManaged(true)
  }

  useEffect(() => {
    (async () => {
      const { status, body } = await prepareProducts()
      if (status === 200) setProducts(body)
    })()
  }, [])

  return (
    <section className="dashboard">
      <div className="container-fluid display-table">
        <div className="row display-table-row">
          <SideBar nav="caminhoes" query={isManaged ? 'organizado' : null} />


          <div className="col-md-6 col-sm-6 display-table-cell v-align">

            <div className="cargas">
              <img src={imgagemEscolhida}/>
            </div>

            <div className="user-dashboard cargas-center">   
              <div className='manage-trains'>
                <button type="button" onClick={manageTrains} data-toggle="modal" data-target="#enviado" className="btn btn-primary">
                  Enviar mercadorias
                </button>
              </div>
            </div>
          </div>
        </div>

      </div>

      <div className="modals">
        <Enviado />
      </div>

    </section>
  )
}

export default Transporte
